import React from 'react'
import './checker.css'

const Checker = () => {
    const a = (data) => {
        let para = document.getElementById('dat')
        // let inpvalue = document.getElementById('inp').value
        const city = data.target.inpu.value
        console.log(data.target.inpu.value)


        fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=12163a331ab780b1f7af8a43b44d24c1`)
            .then(response => response.json())
            .then(data2 => {
                console.log(data2)
                let newvalue = data2['name']
                let tempvalue = data2['main']['temp']

                const a = `${Math.round(tempvalue - 273.15)} °C`
                console.log(a)
                para.innerHTML = newvalue
            })
        data.preventDefault()
            // .catch((err) => {
            //     para.innerHTML = 'Wrong city';
            // })
    }
    return (
            <center>
                <div className='box'>
                    <form onSubmit={a}>
                    <br />
                    <input id="inp" type="text" name="inpu" placeholder="Enter City Name" />
                    <br /><br />
                    <button type="submit" >Check</button>
                    {/* <br /><br /><br /> */}

                </form>
                </div>
            <h1 id="dat"></h1>

            </center>
    )
}

export default Checker
